mapper.insert:
/* ProjectMapper.insertProject */
INSERT INTO TB_PROJECT ( SEQ
                       , GROUP_SEQ
                       , GROUP_NAME
                       , PROJECT_NAME
                       , PROJECT_OWNER
                       , DESCRIPTION
                       , VISIBILITY
                       , CREATE_ID
                       , CREATE_NAME)
VALUES ( #{seq}
       , #{groupSeq}
       , #{groupName}
       , #{projectName}
       , #{projectOwner}
       , #{description}
       , #{visibility}
       , #{createId}
       , #{createName} )

mapper.update:
/* ProjectMapper.updateProject */
UPDATE TB_PROJECT
SET UPDATE_ID = #{updateId}
, UPDATE_NAME = #{updateName}
, UPDATE_DATE = NOW()

    , DESCRIPTION = #{description}


    , VISIBILITY = #{visibility}


    , DEFAULT_BRANCH = #{defaultBranch}


    , LAST_COMMIT_MESSAGE = #{lastCommitMessage}


    , LAST_COMMIT_YMD = #{lastCommitYmd}


    , LAST_COMMIT_HM = #{lastCommitHm}


    , COMMIT_COUNT = #{commitCount}

WHERE SEQ = #{seq}

/* ProjectMapper.deleteProject */
UPDATE TB_PROJECT
SET DEL_YN      = 'Y'
  , DELETE_ID   = #{deleteId}
  , DELETE_NAME = #{deleteName}
  , DELETE_DATE = NOW()
WHERE SEQ = #{seq}
